&lt;SCRIPT&gt;
function setHref() {
ho = document.getElementById(&apos;modify-me&apos;).href;
ho = ho.split(&apos;/&apos;).pop();
document.getElementById(&apos;modify-me&apos;).href = window.location.protocol + &apos;//&apos; + window.location.hostname + &apos;:8080/&apos; + ho;
}
&lt;/SCRIPT&gt;
